class Equipment():
	def __init__(self,Unit):
		self.Unit=Unit
		self.Stats=Unit.Stats

class Weapon():
	def __init__(self,Weapon,Unit,Equipment):
		self.Weapon=Weapon
		self.Unit=WSD[Unit]
		self.Equipment=Equipment
	@property
	def PDMG(self):
		return self.Weapon.PDMG
	@property
	def MDMG(self):
		return self.Weapon.MDMG
	@property
	def ATA(self):
		return self.Weapon.ATA+min(0,(self.Unit.STR-self.Weapon.WT))
	@property
	def WT(self):
		return self.Weapon.WT
	@property
	def Buy(self):
		return self.Weapon.Buy
	@property
	def Sell(self):
		return self.Weapon.Sell

